/**
 * @package    Core
 * @subpackage View
 * @subpackage Helper
 * @name       User Name
 * @category   View Helper
 */
class Core_View_Helper_UserName extends Zend_View_Helper_Abstract
{

    /**
     * Retorna o nome do usuário logado
     * @return string
     */
    public function userName()
    {
        $name = Core_Integration_Sica_User::getUserName();
        return $name;
    }
}